import React, { Component } from "react";
import axios from "axios";

const url = "https://swapi.co/api/people/";

class Swapi extends Component {
	constructor(props) {
		super(props);
		this.state = {
			people: [],
			pagesaatini: 1
		}

		this.pagesebelum = this.pagesebelum.bind(this);
		this.pagesesudah = this.pagesesudah.bind(this);
	}

	async componentDidMount() {
		const res = await axios.get(url)
		this.setState({ people: res.data.results });
	}

	async pagesesudah() {
		console.log('toni')
		if (this.state.pagesaatini === 9) alert('gabisasesudahe');
		else {
		console.log('tono')
		await this.setState({ pagesaatini: this.state.pagesaatini + 1 })
		console.log(this.state.pagesaatini)
		console.log('tano')
		const res = await axios.get('https://swapi.co/api/people/?page=' + this.state.pagesaatini);
			this.setState({ people: res.data.results });
		}
	}

	async pagesebelum() {
		if (this.state.pagesaatini === 1) alert('gabisasebelume');
		else {
			this.setState({ pagesaatini: this.state.pagesaatini - 1 })
			const res = await axios.get('https://swapi.co/api/people/?page=' + this.state.pagesaatini);
			this.setState({ people: res.data.results });
		}
	}
	render() {
		return (
			<div className="container">
				<button onClick={this.pagesebelum}>pagesebelum</button>
				<button onClick={this.pagesesudah}>pagesesudah</button>
				<div className="header">
					<h1>WELCOME TO SWAPIL</h1>
					<h5>May the force be with you.</h5>
				</div>
				<div className="body">
					{this.state.people.map((person, index) =>
						// <Link key={index} to="/about/">About</Link>
						<p key={index = index + 1}> {person.name} <br></br></p>
					)}
				</div>
				<div className="footer">
					<p className="desc">Page 1</p>
				</div>
			</div>
		);
	}
}

export default Swapi;
