import React, { Component } from "react";
import axios from "axios";
import About from "../about";

class Swapi extends Component {
	constructor(props) {
		super(props);
		this.state = {
			people: [],
			currentpage: 1,
			index: 1,
			about: [],
		}
		this.previouspage = this.previouspage.bind(this);
		this.nextpage = this.nextpage.bind(this);
		this.handleClick = this.handleClick.bind(this);
	}

	async componentDindexMount() {
		const res = await axios.get("https://swapi.co/api/people/")
		this.setState({ people: res.data.results });
	}

	/* Create function nextpage handler. */
	async nextpage() {
		if (this.state.currentpage === 9) alert('Currently on the last page.');
		else {
			await this.setState({ currentpage: this.state.currentpage + 1 })
			const res = await axios.get('https://swapi.co/api/people/?page=' + this.state.currentpage);
				this.setState({ people: res.data.results, index: this.state.index + 10 });
		}
	}

	/* Create function previouspage handler. */
	async previouspage() {
		if (this.state.currentpage === 1) alert('Currently on the first page.');
		else {
			await this.setState({ currentpage: this.state.currentpage - 1 })
			const res = await axios.get('https://swapi.co/api/people/?page=' + this.state.currentpage);
			this.setState({ people: res.data.results, index: this.state.index - 10  });
		}
	}

	/* Create function aboutperson handler. */
	aboutperson() {
		if(this.props.isPeople){
			console.log(this.state.people);
		}
		// const res = axios.get('https://swapi.co/api/people/');
		// this.setState({ about: res.data });
		// console.log(res.data);
	}
	handleClick(event) {
		event.preventDefault();
		const mouseClick = event.target.value;
		console.log(event.nativeEvent.which);
    if (mouseClick === 1) {
      // Left mouse click - open in modal ...
    } else if (mouseClick === 3) {
      // Right mouse click - open in new tab ...
      window.open('some url');
    }
  }

	render() {
		return (
			<div className="container">
				{/* Navigation Bar */}
				<div className="navbar">
					<button onClick={this.previouspage}>Previous</button>
					<button disabled={true}>{this.state.currentpage}</button>
					<button onClick={this.nextpage}>Next</button>
				</div>
				{/* Header Page */}
				<div className="header">
					<h1>WELCOME TO SWAPIL</h1>
					<h5>May the force be with you.</h5>
				</div>
				{/* Body Page */}
				<div className="body">
					{this.state.people.map((person, index) =>
						<p key={index = index + this.state.index} onClick={this.aboutperson}> {person.name + " " + index} <br></br></p>
					)}
				</div>
				{/* Footer Page */}
				<div className="footer">
					<p>Page {this.state.currentpage}</p>
				</div>
			</div>
		);
	}
}

export default Swapi;
